import React from "react";
import loginImg from "./../../assets/image/download.jpeg";
// import {From} from 'react-bootstrap';

import { Form, Button } from "react-bootstrap";
import { auth, createuser } from "./../../firebase/firebase.utils";

export class Register extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      userName: "",
      Email: "",
      password: "",
    };
  }
  handleSubmit = async (event) => {
    event.preventDefault();

    const { userName, email, password } = this.state;

    try {
      const { user } = await auth.createUserWithEmailAndPassword(
        email,
        password
      );

      await createuser(user, { userName });

      this.setState({
        userName: "",
        email: "",
        password: "",
        confirmPassword: "",
      });
    } catch (error) {
      console.error(error);
    }
  };
  render() {
    const { userName, email, password } = this.state;
    return (
      <div className="base-container" ref={this.props.containerRef}>
        <div className="header">Register</div>
        <div className="content">
          <div className="image">
            <img src={loginImg} />
          </div>
          <div className="form">
            <div className="form-group">
              {/* <form className='sign-up-form' >
          <FormInput
            
          />
          <FormInput
            type='email'
            name='email'
            value={email}
            onChange={this.handleChange}
            label='Email'
            required
          />
          <FormInput
          
          />
          <Button type='submit'>SIGN UP</Button>
        </form> */}
              <div className="data" onSubmit={this.handleSubmit}>
                <Form.Group >
                  <Form.Label>
                    <label>Username </label>
                  </Form.Label>
                  <Form.Control
                    type="text"
                    name="userName"
                    value={userName}
                    onChange={this.handleChange}
                    label=" Name"
                    required
                  />
                </Form.Group>
              </div>
            </div>
            <div className="form-group">
              <div className="data">
                <Form.Group>
                  <Form.Label>
                    <label>Email </label>
                  </Form.Label>
                  <Form.Control
                    type="email"
                    name="email"
                    value={email}
                    onChange={this.handleChange}
                    label="Email"
                    required
                  />
                </Form.Group>
              </div>
            </div>
            <div className="form-group">
              <div className="data">
                <Form.Group>
                  <Form.Label>
                    <label>Password </label>
                  </Form.Label>
                  <Form.Control
                    type="password"
                    name="password"
                    value={password}
                    onChange={this.handleChange}
                    label="Password"
                    required
                  />
                </Form.Group>
              </div>
            </div>
          </div>
        </div>
        <div className="footer">
          <Button className="btn" type="button">
            Regsiter
          </Button>
        </div>
      </div>
    );
  }
}
