import "./style.scss";
import React from 'react';
import {SaveProfile} from './saveprofile';
import {SetProfile} from './setprofile';
import { Button } from "react-bootstrap";


export class User extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      issaveprofile: true
    };
  }

  componentDidMount() {
    //Add .right by default
    this.rightSide.classList.add("right");
  }

  changeState() {
    const { issaveprofile } = this.state;

    if (issaveprofile) {
      this.rightSide.classList.remove("right");
      this.rightSide.classList.add("left");
    } else {
      this.rightSide.classList.remove("left");
      this.rightSide.classList.add("right");
    }
    this.setState(prevState => ({ issaveprofile: !prevState.issaveprofile }));
  }

  render() {
    const { issaveprofile } = this.state;
    const current = issaveprofile ? "SetProfile" : "SaveProfile";
    const currentActive = issaveprofile ? "SaveProfile" : "SetProfile";
    return (
      <div className="App">
        <div className="SaveProfilepage">
          <div className="containerpage" ref={ref => (this.container = ref)}>
            {issaveprofile && (
              <SaveProfile containerRef={ref => (this.current = ref)} />
              
            )}
            {!issaveprofile && (
              <SetProfile containerRef={ref => (this.current = ref)} />
            )}
            <Saveb
            current={current}
            currentActive={currentActive}
            containerRef={ref => (this.rightSide = ref)}
            onClick={this.changeState.bind(this)}
          />
            
          </div>
        </div>
      </div>
    );
  }
}

const RightSide = props =>{
  return <div className="" ref ={props.containerRef} onClick={props.onClick} >
    <div className="inner-container">
      <div className="text"><Button>{props.current}</Button></div>
    </div>
  </div>

}
